package com.whitesoft.domain;import com.whitesoft.domain.core.AbstractEntity;import org.springframework.util.Assert;import javax.persistence.*;import java.util.HashSet;import java.util.Set;import java.util.UUID;/** * Сущность ОТДЕЛЕНИЕ. * Created by tupichkindenis on 18.09.16. */@Entity@Table(name = "OFFICE")public class Office extends AbstractEntity {    @Column(name = "NAME", nullable = false)    private String name;    @ManyToOne(optional = false)    private Branch branch;    @OneToMany(cascade = CascadeType.ALL, orphanRemoval = true)    @JoinColumn(name = "OFFICE_ID")    private Set<Station> stations = new HashSet<>();    /**     * Ctor.     * @param name     */    public Office(String name, Branch branch) {        Assert.notNull(name);        Assert.hasLength(name);        Assert.notNull(branch);        this.setName(name);        this.setBranch(branch);    }    protected Office(){}    /**     * Add new station.     *     * @param station     */    public void addStation(Station station) {        Assert.notNull(station);        this.stations.add(station);    }    /**     * Returns all stations.     *     * @return     */    public Set<Station> stations() {        return stations;    }    /**     *     * @return     */    public String getName() {        return name;    }    public UUID getBranchId() {return this.branch.getId();}    /**     *     * @param name     */    public void setName(String name) {        this.name = name;    }    /**     *     * @return     */    public Branch getBranch() {        return branch;    }    /**     *     * @param branch     */    public void setBranch(Branch branch) {        this.branch = branch;    }}